import './Expenses.css'
import ExpenseItem from './ExpenseItem'
import Card from '../UI/Card'
import ExpensesFilter from '../ExpensesFilter/ExpensesFilter'
import { useState } from 'react'

const Expenses = (props) => {
    const expense = props.data
    const [year, setYear] = useState('2020')
    const [filteredExpenses, setFilteredExpenses] = useState(expense)
    const pickYearHandler = (selectedYear) => {
        // console.log('Expenses.js')
        // console.log(selectedYear)
        setYear(selectedYear)
        filterExpenses(selectedYear)
    }
    const filterExpenses = (selectedYear) => {
        filteredExpenses = filteredExpenses.filter(exp => exp.date.getFullYear() === selectedYear)
        setFilteredExpenses(filteredExpenses)
    }
    return (
        <div>
            <Card className='expenses'>
                <ExpensesFilter value={year} onPickYear={pickYearHandler}/>
                {filteredExpenses
                .map(ei => <ExpenseItem
                key={ei.id}
                title={ei.title}
                amount={ei.amount}
                date={ei.date} />)
                }    
            </Card>
        </div>
    )
}

export default Expenses
